import React, { useState } from 'react'
import '../Styles/Pages/Login.css'
import Nav from '../Components/Nav'
import { Link } from 'react-router-dom'

function Login() {
    const [formData, setFormData] = useState({});
    const [error, setError] = useState(false);
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();
    
    const [data, setData] = useState({
        email:"",
        password:""
    })

    const handleChange = (e) => {
        setData({...data, [e.target.name] : e.target.value})
    }

    const submitHandler = async(e) => {
        e.preventDefault()
        console.log(data)
        try {
            setLoading(true);
            setError(false);
            const res = await fetch('api/user/login', {
                method: 'POST',
                headers: {
                'Content-Type': 'application/json',
                },
                body: JSON.stringify(data),
            });
            const data = await res.json();
            console.log(data);
            setLoading(false);
            if (data.success === false) {
                setError(true);
                return;
            }
            navigate('/sign-in');
        } catch (error) {
            setLoading(false);
            setError(true);
        }
    }
  return (
    <>
        <div className="nav_holder">
            <Nav />
        </div>
        <div className="login">
            <div className="login_container">
                <div className="login_header">
                    <p>Login </p>
                    <p>Lorem ipsum dolor sit amet.</p>
                </div>
                <form class="login-form" onSubmit={submitHandler}>
                    <div class="input-floating-label">
                        <input class="input" type="email" value={data.email}
                            onChange={handleChange}
                            name="email" placeholder="username" />
                        <label for="input"><ion-icon name="mail-outline"></ion-icon> <span>Email</span></label>
                        <span class="focus-bg"></span>
                    </div>
                    <div class="input-floating-label">
                        <input class="input" type="password" value={data.password} 
                            onChange={handleChange}
                            name="password" placeholder="password" />
                        <label for="input"><ion-icon name="key-outline"></ion-icon><span>Password</span></label>
                        <span class="focus-bg"></span>
                    </div>
                    <button id="submit" class="btn-submit">Login</button>
                </form>
                <p>Create a new Account? <Link to="/register">Register</Link></p>
            </div>
            <div className="login_img">

            </div>
        </div>
    </>
  )
}

export default Login
