import React, { useState } from 'react'
import Nav from '../Components/Nav'
import '../Styles/Pages/Upload.css'


function Upload() {

  const [showElements, setShowElements] = useState(false)
  const [isLegacyYes, setIsLegacyYes] = useState(false);
  const [isLegacyNo, setIsLegacyNo] = useState(false);

  const toggleShowContent = () => {
    setShowElements((p) => !p)
  }

  const handleCheckboxChange = (event) => {
    const { name, checked } = event.target;

    if (name === 'islegacyData') {
      setIsLegacyYes(checked);
    } else if (name === 'notlegacyData') {
      setIsLegacyNo(checked);
    }

    console.log(isLegacyYes, isLegacyNo)
  };

  return (
    <>
        <Nav />
        <div className="upload_wrapper">
          <div className="upload_container">
              <div className="upload_header">
                <h2>Base Material Info</h2>
              </div>
              <form action="">
                {/* Legacy Data */}
                <div className="form_content">
                  <p>Legacy data</p>
                  <label htmlFor="">
                    <input type="checkbox" name="islegacyData" checked={isLegacyYes}
                      onChange={handleCheckboxChange}/> Yes
                  </label>
                  <label htmlFor="">
                    <input type="checkbox" name="notLegacyData" /> No
                  </label>
                </div>

                <div className="form_content legacy_search">
                {/* search DB */}
                  <input type="text" placeholder='Search Database' />
                  <ion-icon name="search-outline"></ion-icon>
                </div>

                <div className="form_content">
                  <div className="btn" onClick={toggleShowContent}>
                    <ion-icon name="add-outline"></ion-icon> Add Element
                  </div>
                </div>

                {
                  showElements && (
                    <div>
                      <h3>Add Element</h3>
                    </div>
                  )
                }

              </form>
          </div>
        </div>
    </>
  )
}

export default Upload
