import React, {useEffect, useState} from 'react'
import { testStandardInfo } from '../../../../features/uploadSlice';
import { useDispatch, useSelector } from 'react-redux';

function Table({ sno, x_axis, y_axis, ManualDataSet, index, excelData, NumberOf_Specimens }) {
    const dispatch = useDispatch()
    const {testStandard} = useSelector(state => state.uploadData)

    const [tableData, setTableData] = useState([
      ['Column 1', 'Column 2'], // Initial row
    ]);

    const handlePaste = (event) => {
      const pastedText = event.clipboardData.getData('text/plain');

      if (!pastedText) return;

      // Determine target column based on click position
      const targetColumn = event.target.cellIndex; // Get index of clicked cell
        console.log(targetColumn, pastedText)
      // Split data into rows and columns, preserving empty columns
      const rows = pastedText
        .split('\n')
        .map((line) => line.trim())
        .filter((line) => line)
        .map((line) => line.split('\t'));
        console.log(rows)
      // Update table data, correctly placing values in target column
      setTableData((prevData) => prevData.concat(rows));

      // dispatch(testStandardInfo(tableData))
    };
    console.log(NumberOf_Specimens)
    console.log("TABLEE", ManualDataSet, sno, index)

    useEffect(() => {
      console.log(index, sno)
    }, [])

  return (
    <>
    {
      NumberOf_Specimens
    }
    <table onPaste={handlePaste}>
      <thead>
        <tr>
          <th>{x_axis}</th>
          <th>{y_axis}</th>
        </tr>
      </thead>
      <tbody className='table_body'>
        {tableData.map((row, index) => (
          <tr key={index}>
            <td><input type="number" value={row[0]} id="" placeholder='Paste your data' /></td>
            <td><input type="number" value={row[1]} id="" /></td>
            {/* <td>{row[1]}</td> */}
          </tr>
        ))}
      </tbody>
    </table>
    </>
  )
}

export default Table
