      // })
      // ab2.shift()
      // a[index][index][index][sno-1] = ab2

      // console.log(a)
    }, [tableData])

  return (
    <>
    <table onPaste={handlePaste}>
      <thead>
        <tr>
          <th>{x_axis}</th>
          <th>{y_axis}</th>
        </tr>
      </thead>
      <tbody className='table_body'>
        {tableData.map((row, index) => (
          <tr key={index}>
            <td><input type="number" value={row[0]} id="" placeholder='Paste your data' /></td>
            <td><input type="number" value={row[1]} id="" /></td>
            {/* <td>{row[1]}</td> */}
          </tr>
        ))}
      </tbody>
    </table>
    </>
  )
}

export default Table






